mapper.select:
SELECT dc.baud_rate AS baudRate, dc.data_bits AS dataBits, dc.stop_bits AS stopBits, dc.parity AS parity, dc.protocol_type AS protocolType,
di.device_type AS deviceType, di.cumulative_usage AS cumulativeUsage, di.device_description AS deviceDescription, di.device_code AS deviceCode, di.device_name AS deviceName, di.status AS status, di.installation_location AS installationLocation FROM user_device_relation udr INNER JOIN device_info di ON udr.device_code = di.device_code LEFT JOIN device_connection dc ON di.device_code = dc.device_code
udr.user_id = #{userId}
AND di.device_type = #{type}
AND di.device_name LIKE CONCAT('%', #{deviceName}, '%')
AND di.status = #{status}
